                    <div className="navbar-text">
                        {login}
                    </div>

                </div>
            </nav>
            {/* <button onClick={setUserEva} >Eva</button>
            <button onClick={setUserAude} >Aude</button> 
            fonctions et setUser sont simples
            */}

            <button onClick={setUserEva} >Eva</button>
            <button onClick={setUserAude} >Aude</button>
        </div>
    );
}

export default Header;
