import React from 'react';
import { useState, useEffect } from 'react';

function Counter(props) {
    const [afficherresultat] = useState(0);

    const incrementer = () => {
        setAfficherResultat(incrementer = afficherresultat + 1);
    }
    const decrementer = () => {

        setAfficherResultat(decrementer = afficherresultat - 1);
    }

        useEffect(() => {
            setAfficherResultat(0);
          }, []);



    return (
        <div>
            <h2>Home</h2>
            <div className="navbar-text">{afficherresultat}</div>
            <button onClick={() =>  setAfficherResultat({incrementer})} >+</button>
            <button onClick={() =>  setAfficherResultat({decrementer})} >-</button>
        </div>
    );
}

export default Counter;
