import React from 'react';
import propTypes from 'prop-types';
import { Link, Input } from 'react-router-dom';


function CreateUser(props) {
    const users = ['MS', 'Iskod', 'Osée', 'Datte']
    const [criteria, setCriteria] = useState('');
    const [filteredUsers, setFilteredUsers] = useState([]);

    const handleSearch = useCallback((event) => {
        setCriteria(event.target.value);
    }, [])

    useEffect(() => {
        setFilteredUsers(users.filter(user => user.toLowerCase().includes(criteria.toLowerCase())))
    }, [criteria])
    return (
        <div className="container">
            <form className="row g-3">
                <div className="col-auto">
                    <input type="text" className="visually-hidden" onChange={handleSearch} placeholder='Recherche'></input>
                </div>
                <div class="col-auto">
                    <input type="text" className="form-control" id="inputPassword2" placeholder="Nouvel utilisateur" />
                </div>
                <div className="col-auto">
                    <button type="submit" className="btn btn-primary mb-3">Créer</button>
                </div>
            </form>
        </div>
    );
}

export default CreateUser;
