 const newTab = [...tab, 9];
    const story = useCallback(() => {
        setStory(counter);
    }, []
    )

    return (
      <div>
        <h2>Other Counter {counter}</h2>
       <div className="navbar-text">{counter % 2 ? 'nombre impair':'nombre pair'}</div>
