import React, { useCallback, useEffect } from 'react';
import { useState } from 'react';
import UserProfile from '../components/UserProfile';

function UserList(props) {

    const users = ['Marie', 'Iskar', 'Osée', 'Hatemadah']
    const [criteria, setCriteria] = useState('');
    const [filteredUsers, setFilteredUsers] = useState([]);

    const handleSearch = useCallback((event) => {
        setCriteria(event.target.value);
    }, [])

    useEffect(() => {
        setFilteredUsers(users.filter(user => user.toLowerCase().includes(criteria.toLowerCase())))
    }, [criteria])


    return (
        <div>
            <h1>Liste des utilisateurs</h1>
            <input type="text" className='form-control'
                onChange={handleSearch}
                placeholder='Recherche' />
            <div className="container">
                <div class="row mt-3">

                    {filteredUsers.map((users, i) => (
                        <div key={i} className="col-lg-3 col-md-4 col-xs-6 mb-3">
                            <UserProfile user={users} />
                        </div>
                    ))}
                    <form className="row g-2">

                        <div className="col-auto">
                            <input type="text" className="form-control" id="inputPassword2" placeholder="Nouvel utilisateur" />
                        </div>
                        <div className="col-auto">
                            <button type="submit" className="btn btn-primary mb-3">Créer</button>
                        </div>
                    </form>
                </div>
            </div>

        </div>
    );
}


export default UserList;
