import React from 'react';
import { useState, useEffect } from 'react';

function Counter(props) {
    const [afficherresultat, setAjouter, setEnlever] = useState(0);

    const incrementer = 0;
    let decrementer = 0;

    useEffect(() => {
        setAjouter(incrementer = afficherresultat + 1);
        setEnlever(decrementer = afficherresultat - 1);

    }, []);



    return (
        <div>
            <h2>Home</h2>
            <div className="navbar-text">{afficherresultat}</div>
            <button onClick={() => setAjouter(+1)} >+</button>
            <button onClick={() => setEnlever(-1)} >-</button>
        </div>
    );
}

export default Counter;
