import React from 'react';
import { useState, useEffect } from 'react';

function Counter(props) {
    const [afficherresultat, setAfficherResultat] = useState(0);

    const incrementer = () => {
        setAfficherResultat(afficherresultat + 1);
    }
    const decrementer = () => {

        setAfficherResultat(afficherresultat - 1);
    }

    useEffect(() => {
        setAfficherResultat(0);
    }, []);
    return (
        <div>
            <h2>Home</h2>
            <div className="navbar-text">{afficherresultat}</div>
            <button onClick={incrementer} className='btn btn-danger me-1' >+</button>
            <button onClick={decrementer} className=''>-</button>
        </div>
    );
}

export default Counter;

